
package ExemplosEstruturado;


public class ExemploIfElse {
    public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean Valor = true;

        if (Valor) {

        	System.out.print("O valor é true");

        }

        else  {

        	System.out.print("O valor é false");

        }


	}

    
}
